
public class LogicalOperators {
	public void logic() {
		//AND(&&)
        System.out.println((5 > 3) && (5 == 5));
		System.out.println((5 < 3) && (5 == 5));
		//OR(||)
		System.out.println((5 > 3) || (5 == 5));
		System.out.println((5 < 3) || (5 == 5));
		
		
	}

	public static void main(String[] args) {
		LogicalOperators obj = new LogicalOperators();
		obj.logic();
		
		// TODO Auto-generated method stub

	}

}
